mapper.select:
		SELECT count(id)
		FROM member
		WHERE nickname = #{nickname} 
    		AND section_id = #{sectionId}

SELECT count(id)
FROM member
WHERE nickname = #{nickname} 
	AND password = #{password}
	AND section_id = #{sectionId}

mapper.delete:
DELETE FROM member
WHERE nickname = #{nickname} 
	AND password = #{password}
	AND section_id = #{sectionId}
